mapper.update:
UPDATE coupon
name = #{name}, type = #{type}, discount_amount = #{discountAmount}, discount_rate = #{discountRate}, min_amount = #{minAmount},
total_count = #{totalCount}, remain_count = remain_count + (#{totalCount} - total_count),
per_limit = #{perLimit}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}
WHERE id = #{id}

mapper.select:
SELECT * FROM coupon


        AND name LIKE CONCAT('%', #{name}, '%')


        AND type = #{type}


        AND status = #{status}


ORDER BY create_time DESC
